CustomCSS:
/* Import Custom Font */
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;600&display=swap');

:root {
  --better-main: #f97d7d !important; /* Primary coral tone */
  --better-sub: #4ECDC4 !important; /* Complementary turquoise */
  --better-alert-highlight: #FFCC00 !important; /* Bright yellow for alerts */

  --text-color: #333333 !important;
  --background-primary: #F7F7F7 !important; /* Light neutral for main background */
  --background-secondary: #FFFFFF !important; /* Pure white for contrast */

  --theme-primary: var(--better-main) !important;
  --theme-secondary: var(--better-sub) !important;
  --text-primary: #1A1A1A;
}

/* General Body Styling */
body {
  font-family: 'Poppins', sans-serif !important;
  background-color: var(--background-primary) !important;
  color: var(--text-primary) !important;
}

/* Buttons */
button {
  background-color: var(--better-main) !important;
  color: #ffffff !important;
  border-radius: 12px !important;
  padding: 10px 2px !important;
  font-weight: 600 !important;
  transition: background-color 0.3s ease;
}

button:hover {
  background-color: var(--better-sub) !important;
}

/* Rounded Icons */
.icon {
  border-radius: 50% !important;
  background-color: var(--better-sub) !important;
  padding: 8px !important;
}

/* Cards */
.card {
  background-color: var(--background-secondary) !important;
  border-radius: 16px !important;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1) !important;
  padding: 20px !important;
}

/* Alerts */
.alert {
  background-color: var(--better-alert-highlight) !important;
  color: #1A1A1A !important;
  border-radius: 10px !important;
  padding: 15px;
  font-weight: 600;
}

/* Links */
a {
  color: var(--better-main) !important;
  text-decoration: none !important;
}

a:hover {
  text-decoration: underline !important;
  color: var(--better-sub) !important;
}
